con:config.script:
properties = testRunner.testCase.testSteps["Properties"];

String[][] args = [
	["Масква", "Москва"],
	["санкт-петербург", "Санкт-Петербург"],
 	["нурманск", "Мурманск"],
 	["самалет", "самолет"],
]

for(int i = 0; i < args.size(); i++){
	properties.setPropertyValue("wrongWord",args[i][0]);
	properties.setPropertyValue("correctWord", args[i][1]);
	testRunner.runTestStepByName("checkText");
	log.info(context.testCase.testSteps["checkText"].testRequest);
}
